useremailjobstatus.map:
function(doc) {
	if(doc.type === 'job' && doc.jobstatus && doc.jobstatus.status){
		  emit([doc.userEmail, doc.jobstatus.status], doc._id);
	}
}

jobstatus.map:
function(doc) {
	if(doc.type === 'job' && doc.jobstatus && doc.jobstatus.status){
		  emit(doc.jobstatus.status, { _id : doc._id, executionserver : doc.executionserver});
	}
}
